const htbUserId = 'USER ID'; 

// Fetching from the API instead of the HTML page
const apiUrl = `https://www.hackthebox.com/api/v4/user/public/profile/${htbUserId}`;

return [{
  json: {
    userId: htbUserId,
    apiUrl: apiUrl,
    profileUrl: `https://www.hackthebox.com/u/${htbUserId}`,
    timestamp: new Date().toISOString(),
    note: "Use an HTTP Request node with the apiUrl next."
  }
}];

// Get the input safely
const input = $input.first().json;

// Since your previous node returned a 'data' object, we'll look there first
const profile = input.data || input;

// Check if we actually have profile data
if (!profile || !profile.name) {
    return [{
        json: {
            error: "Profile data not found. Check the previous node output.",
            receivedKeys: Object.keys(input)
        }
    }];
}

return [{
    json: {
        username: profile.name || 'Unknown',
        fullName: profile.full_name || 'N/A',
        country: profile.country ? profile.country.name : 'Unknown',

        // Note: The public profile API doesn't always include points/rank 
        // unless you use the /profile/content/ endpoint. 
        // If they are missing, we'll default to 0/Unranked.
        rank: profile.rank || 'Unranked',
        points: profile.points || 0,
        userOwns: profile.system_owns || 0,
        rootOwns: profile.root_owns || 0,

        processedAt: new Date().toISOString()
    }
}];

const staticData = $getWorkflowStaticData('global');
const current = $input.first().json;

// 1. Initialize History and Achievement Log if they don't exist
if (!staticData.history) staticData.history = [];
if (!staticData.achievements) staticData.achievements = [];

// 2. Get the most recent snapshot for comparison
const previous = staticData.history.length > 0 
    ? staticData.history[staticData.history.length - 1] 
    : null;

let newAchievementsFound = [];

// 3. Comparison Logic (Only runs if we have a baseline)
if (previous) {
    if (current.userOwns > previous.userOwns) {
        const diff = current.userOwns - previous.userOwns;
        newAchievementsFound.push(`Gained ${diff} User Own(s)`);
    }
    if (current.rootOwns > previous.rootOwns) {
        const diff = current.rootOwns - previous.rootOwns;
        newAchievementsFound.push(`Gained ${diff} System Root(s)`);
    }
    if (current.points > previous.points) {
        newAchievementsFound.push(`Gained ${current.points - previous.points} Points`);
    }
    if (current.rank !== previous.rank) {
        newAchievementsFound.push(`Rank up: ${current.rank}`);
    }
} else {
    // This is the first run! We record the starting point.
    newAchievementsFound.push("HTB Tracker Initialized");
}

// 4. Update Global Achievement Log
if (newAchievementsFound.length > 0) {
    newAchievementsFound.forEach(msg => {
        staticData.achievements.unshift({
            message: msg,
            date: new Date().toISOString()
        });
    });
}

// 5. Save Snapshot if data changed (to prevent duplicate history points)
const hasChanged = !previous || (
    current.userOwns !== previous.userOwns || 
    current.rootOwns !== previous.rootOwns || 
    current.points !== previous.points
);

if (hasChanged) {
    staticData.history.push({
        ...current,
        timestamp: new Date().toISOString()
    });

    // Keep last 50 snapshots for the chart
    if (staticData.history.length > 50) staticData.history.shift();
}

// 6. Keep achievement log manageable
if (staticData.achievements.length > 20) staticData.achievements.pop();

return [{
    json: {
        summary: {
            username: current.username,
            currentPoints: current.points,
            isFirstRun: previous === null,
            newAchievements: newAchievementsFound
        },
        dashboardData: {
            history: staticData.history,
            fullAchievementLog: staticData.achievements
        }
    }
}];

// 1. Get the input safely
const item = $input.first().json;

// 2. Extract values with fallbacks (handles nested or flat structures)
const current = item.current || item.summary || {};
const previous = item.previous || {};
const isFirstRun = item.isFirstRun || (Object.keys(previous).length === 0);

const achievements = [];

// 3. Logic with safety checks
if (isFirstRun) {
    achievements.push({ 
        type: 'initial', 
        message: `Started tracking! Current Machines: ${current.userOwns || 0}` 
    });
} else if (current && previous) {
    // Only compare if both exist and have data
    if ((current.userOwns || 0) > (previous.userOwns || 0)) {
        achievements.push({ message: `${current.userOwns - previous.userOwns} new user flag(s)!` });
    }
    if ((current.rootOwns || 0) > (previous.rootOwns || 0)) {
        achievements.push({ message: ` ${current.rootOwns - previous.rootOwns} new root flag(s)!` });
    }
    if (current.rank && previous.rank && current.rank !== previous.rank) {
        achievements.push({ message: `Rank up: ${current.rank}!` });
    }
}

return [{ 
    json: { 
        ...item, 
        achievements, 
        hasNewAchievements: achievements.length > 0 
    } 
}];

// 1. Get the current data from the previous node
const currentData = $input.first().json.current;

// 2. Access n8n's internal storage for this workflow
const staticData = $getWorkflowStaticData('global');

// 3. Update the storage
staticData.lastKnownProgress = currentData;

// 4. (Optional) Initialize a history array if you want charts later
if (!staticData.history) {
  staticData.history = [];
}

// Only add to history if it's new data
const lastEntry = staticData.history[staticData.history.length - 1];
if (!lastEntry || lastEntry.points !== currentData.points) {
  staticData.history.push({
    ...currentData,
    timestamp: new Date().toISOString()
  });
}

// Keep history manageable (last 100 entries)
if (staticData.history.length > 100) {
  staticData.history.shift();
}

return [{
  json: {
    message: "Data persisted successfully in n8n storage",
    historyCount: staticData.history.length,
    current: currentData
  }
}];

// 1. Get the input safely
const item = $input.first().json;
const current = item.current || {};

// 2. Access n8n's internal "file" storage
const staticData = $getWorkflowStaticData('global');

// 3. Initialize history if this is the first time
if (!staticData.history) {
    staticData.history = [];
}

const today = new Date().toISOString().split('T')[0];

const entry = {
    date: today,
    totalMachines: current.userOwns || 0,
    points: current.points || 0
};

// 4. Update today's record or add a new one (Same logic as your code)
const idx = staticData.history.findIndex(h => h.date === today);

if (idx >= 0) {
    staticData.history[idx] = entry;
} else {
    staticData.history.push(entry);
}

// 5. Keep history manageable (last 30 days)
if (staticData.history.length > 365) {
    staticData.history.shift();
}

// 6. Return everything so the next node can use it
return [{ 
    json: { 
        ...item, 
        history: staticData.history 
    } 
}];

// 1. Get the input safely
const item = $input.first().json;
const current = item.current || item.summary || item;
const achievements = item.achievements || [];
const history = item.history || [];

// --- 2. DATA PROCESSING ---

// OS Breakdown & Difficulty Distribution
const osData = { Windows: 0, Linux: 0 };
const diffData = { Easy: 0, Medium: 0, Hard: 0, Insane: 0 };
let totalStars = 0;

achievements.forEach(a => {
    if (osData.hasOwnProperty(a.os)) osData[a.os]++;
    if (diffData.hasOwnProperty(a.difficulty)) diffData[a.difficulty]++;
    // Assuming stars/rating is provided in the achievement object
    totalStars += (a.stars || 0);
});

const avgDifficulty = achievements.length ? (totalStars / achievements.length).toFixed(1) : 0;

// Monthly Completions (Grouping history by Month)
const monthlyStats = {};
history.slice(-12).forEach(h => {
    const month = h.date.substring(0, 7); // Gets "YYYY-MM"
    monthlyStats[month] = (monthlyStats[month] || 0) + 1;
});

// --- 3. GENERATE HTML ---
const html = `
<!DOCTYPE html>
<html>
<head>
    <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
    <style>
        body { background:#0a0e27; color:white; font-family:sans-serif; padding:20px; display:flex; flex-direction:column; align-items:center; }
        .grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 20px; width: 100%; max-width: 1000px; }
        .card { background:rgba(255,255,255,0.05); padding:20px; border-radius:10px; border: 1px solid #9fef0033; }
        .stat-val { color: #9fef00; font-size: 24px; font-weight: bold; }
        table { width: 100%; border-collapse: collapse; margin-top: 15px; font-size: 13px; }
        th { text-align: left; color: #9fef00; padding: 8px; border-bottom: 1px solid #333; }
        td { padding: 8px; border-bottom: 1px solid #1a1a1a; }
        canvas { width: 100% !important; max-height: 200px; }
        h1, h3 { color: #9fef00; margin-top: 0; }
    </style>
</head>
<body>
    <h1>${current.username || 'HTB'} Dashboard</h1>

    <div class="grid">
        <div class="card">
            <h3>Overview</h3>
            <p>Avg. Machine Rating: <span class="stat-val">${avgDifficulty}â˜…</span></p>
            <p>Windows vs Linux: <span class="stat-val">${osData.Windows} / ${osData.Linux}</span></p>
        </div>

        <div class="card">
            <h3>OS Distribution</h3>
            <canvas id="osChart"></canvas>
        </div>

        <div class="card">
            <h3>Monthly Activity</h3>
            <canvas id="monthlyChart"></canvas>
        </div>

        <div class="card" style="grid-column: span 1;">
            <h3>Recent Completions</h3>
            <table>
                <thead><tr><th>Name</th><th>OS</th><th>Diff</th></tr></thead>
                <tbody>
                    ${achievements.slice(0, 5).map(a => `
                        <tr><td>${a.name}</td><td>${a.os}</td><td>${a.difficulty}</td></tr>
                    `).join('')}
                </tbody>
            </table>
        </div>
    </div>

    <script>
        // OS Pie Chart
        new Chart(document.getElementById('osChart'), {
            type: 'pie',
            data: {
                labels: ['Windows', 'Linux'],
                datasets: [{
                    data: [${osData.Windows}, ${osData.Linux}],
                    backgroundColor: ['#00a3ff', '#9fef00']
                }]
            },
            options: { plugins: { legend: { labels: { color: 'white' } } } }
        });

        // Monthly Bar Chart
        new Chart(document.getElementById('monthlyChart'), {
            type: 'bar',
            data: {
                labels: ${JSON.stringify(Object.keys(monthlyStats))},
                datasets: [{
                    label: 'Completions',
                    data: ${JSON.stringify(Object.values(monthlyStats))},
                    backgroundColor: '#9fef00'
                }]
            },
            options: { 
                scales: { 
                    y: { ticks: { color: 'white' } },
                    x: { ticks: { color: 'white' } }
                }
            }
        });
    </script>
</body>
</html>`;

return [{ json: { dashboard: html } }];
